package com.tangem.rdf.normalization;

/**
 * The output of the Hash N-Degree Quad algorithm.
 *
 * @author Simon Greatrix on 08/10/2020.
 */
class NDegreeResult implements Comparable<NDegreeResult> {

  /** The hash generated by the Hash N-Degree Quads algorithm. */
  private final String hash;

  /** The identifier issuer used in generating this result. */
  private final IdentifierIssuer issuer;


  NDegreeResult(String hash, IdentifierIssuer issuer) {
    this.hash = hash;
    this.issuer = issuer;
  }


  @Override
  public int compareTo(NDegreeResult o) {
    return hash.compareTo(o.hash);
  }


  @Override
  public boolean equals(Object o) {
    if (this == o) {
      return true;
    }
    if (!(o instanceof NDegreeResult)) {
      return false;
    }
    return getHash().equals(((NDegreeResult) o).getHash());
  }


  /**
   * Get the hash generated by the Hash N-Degree Quads algorithm.
   *
   * @return the hash
   */
  public String getHash() {
    return hash;
  }


  /**
   * Get the issuer used by the algorithm to produce the hash.
   *
   * @return the issuer
   */
  public IdentifierIssuer getIssuer() {
    return issuer;
  }


  @Override
  public int hashCode() {
    return getHash().hashCode();
  }

}
